// Generated by CoffeeScript 1.6.3
window.mySwipe = Swipe(document.getElementById('gallery'));

$(function() {
  $('#slider-range').slider({
    range: true,
    min: 0,
    max: 500,
    values: [75, 300],
    slide: function(event, ui) {
      return $('#amount').val('$' + ui.values[0] + ' - $' + ui.values[1]);
    }
  });
  $('#amount').val('$' + $('#slider-range').slider('values', 0) + ' - $' + $('#slider-range').slider('values', 1));
  $('.md-trigger').click(function() {
    return false;
  });
  $('.loop iframe').wrap('<figure class="video"></figure>');
  $('.loop > p > img, .loop > img, article > img').wrap('<figure></figure>');
  $('.video').fitVids();
  $.stellar({
    horizontalScrolling: false
  });
  $(document).on('click', '.js-reveal', function() {
    var target;
    target = $(this).attr('href');
    $(target).toggleClass('opened');
    if ($(this).hasClass('js-sign-in-opener')) {
      if ($(target).hasClass('opened')) {
        $('#username').focus();
      }
    }
    return false;
  });
  $('.js-show-pw').click(function() {
    var type;
    type = $(this).parents('.field').find('input');
    if ($(type).attr('type') === 'text') {
      $(type).get(0).type = 'password';
      $(this).text('Show password');
    } else {
      $(type).get(0).type = 'text';
      $(this).text('Hide password');
    }
    return false;
  });
  return $('.check-buttons input').change(function() {
    var check;
    check = $(this).parent('label');
    check.toggleClass('checked');
    if (check.hasClass('checked')) {
      return $(this).siblings('.livicon').updateLivicon({
        c: '#55BA7F'
      });
    } else {
      return $(this).siblings('.livicon').updateLivicon({
        c: '#fff'
      });
    }
  });
});
